INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (21,'Dornsife','American Studies and Ethnicity','101',4,' Race and Class in Los Angeles ','Analysis of race and the economic, political, gender, and social dimensions of contemporary Los Angeles including topics such as residential segregation, economic inequality, and city politics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (22,'Dornsife','American Studies and Ethnicity','135',4,' Peoples and Cultures of the Americas ','An introduction to cultures and people in the Americas; the social, historical, economic and cultural formations that together make up the Latino/a American imaginary.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (23,'Dornsife','American Studies and Ethnicity','200',4,' Introduction to American Studies and Ethnicity ','Introduction to American studies and ethnic studies. Provides an overview of major theories, concepts, and issues.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (24,'Dornsife','American Studies and Ethnicity','205',4,' Introduction to American Popular Culture ','Examines the relation between U.S. national culture, race, and popular culture.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (25,'Dornsife','American Studies and Ethnicity','252',4,' Black Social Movements in the U.S. ','This course examines black social movements for freedom, justice, equality, and self determination. Beginning with Reconstruction, movements include labor, civil rights, radical feminism, socialism, reparations, Black Nationalism, prisoners\' rights, and Hip Hop.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (26,'Dornsife','American Studies and Ethnicity','274',4,' Exploring Ethnicity Through Film ','Examination of the constructions of American ethnicity/race in film.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (27,'Dornsife','American Studies and Ethnicity','285',4,' African American Popular Culture ','Examines history of popular cultural forms such as literature, music, dance, theater, and visual arts produced by and about African Americans.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (28,'Dornsife','American Studies and Ethnicity','301',4,' America, the Frontier, and the New West ','Introduction to an interdisciplinary study of American political, cultural and social life with a particular emphasis on the Western United States as a region. <strong>Recommended preparation:</strong> HIST 200, ENGL 263',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (29,'Dornsife','American Studies and Ethnicity','336',4,' Re-Viewing Religion in Asian America ','Interdisciplinary analysis of the religions traditions, institutions, and experiences of Asians and Pacific Islanders in the U.S.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (30,'Dornsife','American Studies and Ethnicity','343',4,' Food, Health and Culture in Los Angeles ','Examination of Los Angeles diverse food cultures as well as the food justice issues that affect many low-income residents of neighborhoods surrounding USC campus.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (31,'Dornsife','American Studies and Ethnicity','345',4,' Law and American Indian Studies ','Examines indigenous sovereignties and the historical andcontemporary legal histories and cases which inform them.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (32,'Dornsife','American Studies and Ethnicity','350',4,' Junior Seminar in American Studies and Ethnicity: Theories and Methods ','Advanced study in interdisciplinary theories and methods for analyzing race and ethnicity in the United Stes, including a comparative study of topics such as inequality, gender, and class.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (33,'Dornsife','American Studies and Ethnicity','357',4,' Latino Social Movements ','Focuses on the political experience of Latinos in the U.S. Comparative analysis of their political experiences and perspectives, their histories of identity formation, and their political organizations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (34,'Dornsife','American Studies and Ethnicity','392',2,' Undergraduate Research Methods ','Examination of processes of scholarly research; quantitative and qualitative research methods; faculty mentorship; experiential learning; research proposal writing; careers in research. Sophomore or junior standing in the major. Departmental approval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (35,'Dornsife','American Studies and Ethnicity','442',4,' American Literature, 1920 to the Present ','American poetry, fiction, and drama since World War I with special attention to Eliot, Frost, Hemingway, Fitzgerald, O\'Neill, Stevens, Faulkner, and Nabokov.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (36,'Dornsife','American Studies and Ethnicity','444',4,' Native American Literature ','Survey of Native American literature, including oral traditions and print genres, such as short story, poetry, novel, and autobiography, from 1700 to the present. <strong>Recommended preparation:</strong> ENGL 263.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (37,'Dornsife','American Studies and Ethnicity','449',4,' Asian American Literature ','Survey of Asian American literature from the earliest time to the present; development of prose, poetry, and novel.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (38,'Dornsife','American Studies and Ethnicity','458',4,' Race and Ethnicity in Entertainment and the Arts ','Examines how race and ethnicity as social categories are shaped by communication media; focuses on how race and ethnicity sustain entertainment and media industries. Prerequisite: COMM 206.',1);
