INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (41,'Dornsife','American Studies and Ethnicity','500',4,' Introduction to American Studies and Ethnicity ','An exploration of themes, theoretical influences, and methodological approaches current in American studies and ethnic studies. Open to first-year graduate students in American Studies and Ethnicity only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (42,'Dornsife','American Studies and Ethnicity','562',4,' The Practice of Ethnography ','Major approaches to ethnographic fieldwork are explored in classic cases.',1);
