INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (53,'Dornsife','Anthropology','140',4,' Mesoamerican Cosmovision and Culture ','An exploration of the nature and contributions of pre-Columbian high civilizations (Maya, Aztecs, etc.) and their descendants as they resist and assimilate to the modern world.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (54,'Dornsife','Anthropology','200',4,' The Human Animal ','Foundations of the human species. Examination of scientific evidence from Darwinian theory, primate behavior, fossils, and the behavior of modern people. Laboratory. Duplicates credit in former ANTH 200Lg.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (55,'Dornsife','Anthropology','201',4,' Principles of Human Organization ','Major culture types, nomadic hunters and herders, peasant andtribal societies, sophisticated kingdoms; social, political,economic, and religious institutions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (56,'Dornsife','Anthropology','202',4,' Archaeology: Our Human Past ','Archaeology as the means of investigating our shared human past, from the origins of humanity to the foundations of current civilization.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (57,'Dornsife','Anthropology','205',4,' Introduction to Global Studies and Overseas Research ','Cultural differences and social processes examined in global and regional networks. Issues are studied ethnographically, using materials from several disciplines.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (58,'Dornsife','Anthropology','250',4,' Race and Sexual Politics in Southeast Asia ','Southeast Asia is studied as a meeting place of different races and cultural traditions, with emphasis on the precolonial heritage of sexual equality and postcolonial reinterpretations of men\'s and women\'s worlds.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (59,'Dornsife','Anthropology','263',4,' Exploring Culture Through Film ','Concepts of social anthropology using filmic representations ofsocieties throughout the world in contrast to written ethnography.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (60,'Dornsife','Anthropology','300',4,' Evolution, Ecology, and Culture ','The roles of biology, culture, and the environment in shaping human society, integrating evolutionary biology and cultural theory. Duplicates credit in former ANTH 300.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (61,'Dornsife','Anthropology','314',4,' The Nature of Maya Civilization ','A seminar forum on Maya culture from the earliest form to thepresent; problems of origins, classic florescence, systemscollapse, conquests, persistance, and transformation today.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (62,'Dornsife','Anthropology','316',4,' North American Indians in American Public Life ','Role of American Indians in American public life from colonialtimes to the present; native American forms of government;relations between tribes and the U.S.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (63,'Dornsife','Anthropology','360',4,' Symbolic Anthropology ','The role of symbols in the evolution of culture; symbolic aspects of myth, ritual, and social life. Prerequisite: sophomore standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (64,'Dornsife','Anthropology','370',4,' Sex, Love, and Marriage: An Introduction to Kinship ','Comparative examination of family and kinship in tribal, peasant, and complex societies, emphasizing non-Western cultures, societal and normative consequences of forms and functions in family.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (65,'Dornsife','Anthropology','372',4,' Interpretation of Myth and Narrative ','Oral narratives from non-Western cultures; communications about deeply-held moral beliefs, psychological tensions, social problems, and the structure of the mind.',1);
