INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (67,'Dornsife','Anthropology','410A',4,' Ethnographic Field Methods and Practicum ','Survey of anthropological methods for acquiring and analyzing data. Ethnographic research methods of modes of analysis: development of a field research project.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (68,'Dornsife','Anthropology','415',4,' Global Studies Senior Seminar ','Preliminary analysis for research data for overseas research to compare results, discuss writing strategies and gain comparative perspective to prepare a senior thesis. <strong>Recommended preparation:</strong> ANTH-205, ANTH-325.',1);
