INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (82,'Dornsife','Arabic','322',4,' Advanced Arabic I ','Advanced expression in written and spoken Arabic, including reading of original texts, understanding different registers, and exposure to modern Arabic cultures. Prerequisite: ARAB 252. (Duplicates credit in former MDES 322)',1);
