INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (88,'Dornsife','Art History','330',4,' Medieval Art ','Introductory survey of art and architecture of Christianity from 300-1300; biblical themes and classical traditions; cultural and historical analysis of medieval art.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (89,'Dornsife','Art History','366',4,' Picturing Democracy: American Art and Visual Culture, 1750-1900 ','Studies the art and visual culture of the United States from nation formation to the Worlds Columbian Exposition of 1893.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (90,'Dornsife','Art History','368',4,' Modern Art I: 1700-1850 ','A cultural and historical examination of European art and architecture from 1700 (Rococo) to 1850 (Realism), focusing on the beginnings of modernism in the age of revolution.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (91,'Dornsife','Art History','373',4,' History and Theory of Photography ','Explores key moments in the history of photography from its invention to the present. Issues include modernity and mass culture; photography as a fine art; technologies of vision.',1);
