INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (128,'Dornsife','Biological Sciences','220',4,' General Biology: Cell Biology and Physiology ','In-depth survey of key topics related to advances in our knowledge of cellular biology and physiology; cell composition/metabolism; gene action; organism structure and function. <strong>Recommended preparation:</strong> high school chemistry; BISC 120L or BISC 121L.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (129,'Dornsife','Biological Sciences','230',4,' The Biology of the Brain ','The structure and function of the mammalian brain including the role of the brain in regulating behavior, both in normal and diseased states; in relation to mind; and in comparison with machine forms of intelligence.',1);
