INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (377,'Dornsife','Economics','593',2,' Practicum in Teaching the Liberal Arts ','Practical principles for the long-term development of effective teaching within college disciplines. Intended for teaching assistants in Dornsife College. Open only to doctoral students in Economics. Graded CR/NC.',1);
