INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (399,'Dornsife','Economics','694',2,' Seminar in Dynamic Economics ','Topics in dynamic economics involving business fluctuations, economic growth and development, microeconomic adjustments and market mechanisms; related quantitative and qualitative methods; empirical research involving economic change. Graded CR/NC.',1);
