INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (432,'Dornsife','English','444',4,' Native American Literature ','Survey of Native American literature, including oral traditions and print genres, such as short story, poetry, novel, and autobiography, from 1700 to the present. <strong>Recommended preparation:</strong> ENGL 263.',1);
