INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (444,'Dornsife','English','596',1,' Internship for Curricular Practical Training ','Part-time or full-time practical work experience in the student\'s field of study. The internship must be located at an off-campus facility. Students are individually supervised by faculty. May not be taken until the student has completed at least on semester of enrollment in the graduate program.',1);
