INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (447,'Dornsife','English','603',4,' The Editorial Experience: The Craft of Publication ','An intensive workshop in applied English, coordinating literary analysis with editing and publication, including relationships with authors; academic and trade presses; journals; editing, and design.',1);
