INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (471,'Dornsife','Environmental Studies Program','495',4,' Senior Seminar in Environmental Studies ','Students form multidisciplinary teams and are asked to study and resolve a major environmental problem facing a particular region or target population.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (472,'Dornsife','Environmental Studies Program','500',4,' Interdisciplinary Approaches to Environmental Studies ','Students will develop advanced skills to address complex environmental issues that society faces today. Different approaches for studying environmental processes and resources will be examined.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (473,'Dornsife','Environmental Studies Program','540',4,' California Coastal Zone Science and Policy ','Science and policy issues used to characterize and manage California coastal resources. Key issues include: coastal pollution, public health, ecosystem management, and marine reserves. <strong>Recommended preparation:</strong> ENST-500.',1);
