INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (480,'Dornsife','French','235',2,' Intermediate Conversational French ','Designed for non-majors/minors interested in maintaining and developing French language competency. Builds vocabulary, ease of communication, and cultural knowledge through discussion of contemporary topics. Graded Credit/No Credit. Not available for credit to French majors. Not open to French majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (481,'Dornsife','French','250',4,' French IV ','Introduction to French literature through the study of texts and audiovisuals organized around a central theme; develops close-reading techniques and discursive skills; reviews French grammar. Prerequisites: FREN 220 or French placement exam.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (482,'Dornsife','French','300',4,' French Grammar and Composition ','Grammatical structure and vocabulary building with practical application to written composition. Prerequisites: FREN 250.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (483,'Dornsife','French','320',4,' The French New Wave and its Legacy ','Film-making in France from the earliest experiments to current trends. Emphasis on the political, social, historical context of French films. Taught in English. Reading knowledge of French recommended.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (484,'Dornsife','French','330',4,' Critical Writing in French ','Advanced composition through critical reading of literary texts; comprehensive analysis of difficult grammatical structures and stylistics. Prerequisite: FREN 300.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (485,'Dornsife','French','368',4,' French Foreign Policy: 1945 to the Present ','Introduction to historical, thematic perspectives of French foreign policy since 1945 including review of external and internal constraints influencing foreign policy. (Paris semester only).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (486,'Dornsife','French','403',4,' Studies in Colonialism and Postcolonialism ','Study and analysis of colonialism and postcolonialism in French and Francophone contexts. Prerequisite: FREN-330.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (487,'Dornsife','French','445',4,' Studies in Gender, Feminism and Sexuality ','Major feminist thinkers and writers viewed from the perspective of the evolution of gender and sexuality in contemporary France. Conducted in French. Prerequisite: FREN 330.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (488,'Dornsife','French','449',4,' Studies in French Civilization ','(Paris Semester only) An analysis of the prestige of Paris, past and present, based upon close examination of literary texts and graphic materials, and visits to sites and monuments. <strong>Recommended preparation:</strong> FREN 300',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (489,'Dornsife','French','464',4,' Colloquium: French Civilization ','Selected topics such as the press, educational institutions, French cinema today, and French colonial history. Conducted in French. Prerequisites: FREN 330.',1);
