INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (498,'Dornsife','Geological Sciences','105',4,' Planet Earth ','Geologic structure and evolution of planet earth. Principles ofplate tectonics, rocks and minerals, processes of mountainbuilding, continent and ocean formation, earthquakes, volcanism,development of landforms by running water and glaciers. Lecture, 3hours; laboratory, 2 hours. One all-day or two-day field triprequired.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (499,'Dornsife','Geological Sciences','107',4,' Oceanography ','Physical, chemical, and geological character of the oceans andocean basins. Origin of the oceans. Ocean processes and agents.Economic value of the oceans. Lecture, 3 hours; laboratory, 2 hours. One all-day field trip required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (500,'Dornsife','Geological Sciences','108',4,' Crises of a Planet ','Impact of civilization on planet earth, and impact of earth\'s natural evolution on society: earthquakes, volcanism, landslides, floods, global warming, acid rain, groundwater depletion and pollution; mineral and fossil fuel depletion, formation of the ozone hole. Lecture, 3 hours; laboratory, 2 hours. One all-day or overnight field trip.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (501,'Dornsife','Geological Sciences','150',4,' Climate Change ','Climate systems from the beginning of earth history to the present; tools and techniques used to reconstruct prehistoric climate records; effects of climate variations on development of life forms on earth.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (502,'Dornsife','Geological Sciences','160',4,' Introduction to Geosystems ','Survey of natural geological/ environmental processes (systems) and variability active near the earth\'s surface in the region that houses most life (the biosphere).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (503,'Dornsife','Geological Sciences','240',4,' Earthquakes ','Causes of earthquakes and nature of large faults; earthquake hazard and risk; world\'s great earthquakes; understanding the Richter scale. Lecture, 3 hours; laboratory, 2 hours; one fieldtrip required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (504,'Dornsife','Geological Sciences','290',1,' Special Laboratory ','Laboratory component for GEOL 105Lg, 107Lxg, 108Lg, 125Lxg, 130Lxg, 150Lxg, or 240Lxg for students with equivalent lecture credit from another institution.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (505,'Dornsife','Geological Sciences','315',4,' Minerals and Earth Systems ','Minerals and their formation in Earth geosystems; includes discussions of mineral properties, crystal structures, uses and biogeochemical importance. Lecture, 3 hours; laboratory, 6 hours; required field trips. <strong>Recommended preparation:</strong> any introductory GEOL course.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (506,'Dornsife','Geological Sciences','432',2,' Introduction to Field Techniques ','Introduction to field techniques used in a variety of Earth Science disciplines. Field and digital techniques for map making including use of GIS, GPS, and digital tablets. Field analyses of rocks and rock structures. Map interpretation and digital processing of field data. Includes weekly labs and some weekend field trips.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (507,'Dornsife','Geological Sciences','433',4,' Paleontology and Evolution in Deep Time ','Origin and evolution of life; Precambrian life; evolutionary history of major groups during the Phanerozoic; mass extinctions; deep time and evolutionary processes. <strong>Recommended preparation:</strong> any introductory GEOL course. Lecture, 3 hours; laboratory, 3 hours; required field trips.',1);
