INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (621,'Dornsife','Human Biology','420',4,' Applied Human Physiology ','The physiology of the respiratory, cardiovascular, renal, musculoskeletal, digestive, endocrine, and nervous systems in humans, with application to human health and pathology. Prerequisite: BISC-220. (Duplicates credit in former EXSC 405)',1);
