INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (638,'Dornsife','International Relations','213',4,' The Global Economy ','Economic and political concepts necessary to the understanding of the modern global economy. Topics include international trade, foreign investment and migration.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (639,'Dornsife','International Relations','303',4,' Leadership and Diplomacy ','The role of leaders, diplomatic leadership and creativity in statecraft, providing a deep understanding of the theoretical and practical dimensions of diplomacy.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (640,'Dornsife','International Relations','304',4,' National Intelligence and the Shaping of Policy ','The focus is intelligence to improve the making of policy, with attention to collection; overt and covert, operations; domestic intelligence; and oversight in democracies.',1);
