INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (667,'Dornsife','International Relations','424',4,' Citizenship and Migration in International Politics ','Changing notions of citizenship in the context of history, and of economic, political and sociological theories of international migration; diaspora and migration case studies.',1);
