INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (669,'Dornsife','International Relations','439',4,' Political Economy of Russia and Eurasia ','Interaction of politics and economics in the former Soviet Union and its component republics; the historical planned economy, the politics of reform and political economy of former Soviet foreign relations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (670,'Dornsife','International Relations','449',4,' International Courts and the Globalization of Law ','Research-based analysis of dramatic development of international courts, exploring causes for their development, autonomy and political relations with states, compliance, effects on international relations.',1);
