INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (805,'Dornsife','Multidisciplinary Activities','450',4,' Individual Program of Study ','An individual educational project approved by a faculty committee, combining directed research with internships, service learning, artistic or literary production, and/or other relevant educational activities. Open only to sophomore, junior or senior standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (806,'Dornsife','Multidisciplinary Activities','460',4,' Collaborative Learning Project ','A project approved by a faculty committee, requiring students to collaborate on research or an original work in the literary, plastic or performing arts. Open only to sophomore, junior or senior standing. Graded CR/NC.',1);
