INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (811,'Dornsife','Middle East Studies','314',4,' Political Thought in the Middle East ','Major strands of Islamic political thought from early Islam through the classical Islamic period to contemporary developments and transformations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (812,'Dornsife','Middle East Studies','325',4,' Politics of Film and Literature in Modern Iran ','Explores contemporary Iranian society through the study of social tensions created by poverty-wealth, tradition-modernity, masculinity-femininity, and notions of religio-political authority as reflected in the literature and films of the country.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (813,'Dornsife','Middle East Studies','345',4,' Power and Authority in the Middle East ','A comparative examination of social relations of power and institutions with a special focus on intellectual traditions, the law, and social movements.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (814,'Dornsife','Middle East Studies','363',4,' Middle East Political Economy ','Examination of general economic development issues: population, agriculture, industrialization, trade, oil, etc. Several Mideast case study countries are then explored in depth.',1);
