INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (923,'Dornsife','Physics','492',4,' Senior Lab ','Projects will include experiments in mechanics, thermodynamics, electricity and magnetism. Emphasis on laboratory work with discussion of theoretical background. Lecture, 2 hours; laboratory, 6 hours.',1);
