INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (928,'Dornsife','Physics','510',3,' Methods of Theoretical Physics ','Vector analysis; infinite, asymptotic Fourier series; complete sets; Dirac delta function; Fourier, Laplace transforms; Legendre functions; spherical harmonics; Sturm-Liouville theory; orthogonal polynomials; gamma-factorial function; complex variables.',1);
