INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (975,'Dornsife','Political Science','323',4,' Applied Politics ','Provides students with the knowledge and skills necessary to become active in politics based on understanding the history, theory, and practices of public participation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (976,'Dornsife','Political Science','334',4,' Interest Groups and Elite Behavior ','Introduction to interest group and elite views of the American system, including recent interest group theory and findings and the general critiques of power distribution in American society.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (977,'Dornsife','Political Science','351',4,' Middle East Politics ','Political development in the Middle East, emphasizing historical, cultural, and socioeconomic conditions affecting political structures and functions; modernization and countervailing social, economic and religious forces.',1);
