INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (994,'Dornsife','Political Science','450',4,' Political Development ','Choice of models in nation- building; party and other means of mass mobilization; elite recruitment and differentiation; peculiarities of cultures and subcultures; integration of ethnic and other minorities; political socialization and secularization; legitimization.',1);
